.data
	words: 		.asciiz ""
	wordCount: 	.word	3120
	
	Welc01: 	.asciiz "Cows and Bulls: Word Version\n"
	Welc02: 	.asciiz "Authors: \n\n\n\n"
	Menu00: 	.asciiz "Choose from the following:\n\n"
	Menu01: 	.asciiz "[1] Start a new game\n"
	Menu02:		.asciiz "[2] How to play\n"
	Menu03:		.asciiz "[3] Exit game\n"
	mPrompt:	.asciiz "Please enter a choice [1-3]: "
	MenuErr:	.asciiz "That was not a valid menu option."
	exit:		.asciiz "Now exiting the program..."
	
	Guess00:	.asciiz "Enter a 4 letter word with no repeating letters:\n"
	Guess01:	.asciiz "Valid input is lowercase letters a-z. \n"
	Guess02:	.asciiz "Enter STOP to give up.\n"
	GuessE01:	.asciiz "Answer must contain 4 letters."
	GuessE02:	.asciiz "Answer cannot contain repeated letters."
	GuessE03:	.asciiz "Input has already been guessed."
	GuessE04:	.asciiz "An invalid character was used."
	uGuess:		.asciiz ""
	
	Answer:		.asciiz "    "
	return:		.asciiz "\n"
	MenuChoice:	.space 64
	Inst:		.asciiz "Welcome to the Bulls and Cows: Word Edition game. \n\nThe object of the game is to correctly guess \na randomly generated 4 letter word. \nFor each guess, the computer will return \none of three options. \n\nA 'Bull' indicates that a letter you guessed \nis correct and in the correct position within the \nmystery word. \n\nA 'Cow' indicates that a letter you guessed \nis in the mystery word, however it is in a different \nlocation. \n\nA 'Miss' indicates that the letter \nyou guessed is not in the word at all. \n\nTo make a valid guess, enter a 4 letter word from the \nenglish language with no repeating letters.\n\n " 
	
	Score01:	.asciiz "\nYour guess has "
	Score02:	.asciiz "has "
	bScore01:	.asciiz " Bull and "
	bScore02:	.asciiz " Bulls and "
	cScore01:	.asciiz " Cow \n"
	cScore02:	.asciiz " Cows \n"
	
	quit:		.asciiz "You gave up... The word to guess was: "
	win:		.asciiz "\nCongradulations! You won!\n"
	time01:		.asciiz "It took you "
	time02:		.asciiz " seconds\n"
	time03:		.word 86400000		# miliseconds per day
	
	beep: 		.byte 72
	duration: 	.byte 100
	volume: 	.byte 127
	
.text
	

main:
	la 	$t0, Welc01
	la 	$t9, return			#load new line char into t9 reg so we can use frequently
	li 	$v0, 4
	add 	$a0, $t0, $zero			#load address of $t0 to $a0
	syscall					#call prints string
	la 	$t0, Welc02
	add 	$a0, $t0, $zero			#load second message to $a0
	syscall					#print it
	jal 	ShowMenu			#jump to showmenu function and show the menu...	
						
	

gameLoop:
	#generate a random number
	lw	$a1, wordCount
	li	$v0, 42				
	syscall
	
	#use number to select a word from the list
	sll 	$a0, $a0, 2			# multiply random number by 4 to account for word lengths
	la 	$t0, words			# $t0=start address of words data
	la	$t1, Answer
	add 	$t0, $t0, $a0			# $a0 is start position of words
	lb	$s0, ($t0)
	sb	$s0, ($t1)
	addi	$t0, $t0, 1
	addi	$t1, $t1, 1
	lb	$s0, ($t0)
	sb	$s0, ($t1)
	addi	$t0, $t0, 1
	addi	$t1, $t1, 1
	lb	$s0, ($t0)
	sb	$s0, ($t1)
	addi	$t0, $t0, 1
	addi	$t1, $t1, 1
	lb	$s0, ($t0)
	sb	$s0, ($t1)
	addi	$t0, $t0, 1
	addi	$t1, $t1, 1
#	move 	$t1, Answer			# $s0 = random word selected by computer
	li 	$v0, 4				
	la 	$t0, Answer
	add 	$a0, $t0, $zero
	syscall	
	#game begins
	li 	$v0, 4				
	la 	$t0, Guess00
	add 	$a0, $t0, $zero
	syscall					#syscall displays guess0 prompt to user
	la 	$t0, Guess01
	add 	$a0, $t0, $zero
	syscall					#displays guess1 prompt
	la 	$t0, Guess02
	add 	$a0, $t0, $zero
	syscall					#displays guess2 prompt
	li 	$v0, 8
	la 	$a0, uGuess
	li 	$a1, 64
	syscall					#stores input to uGuess variable
	la 	$t0, uGuess
	#li 	$v0, 4				#print
	#add 	$a0, $t0, $zero
	#syscall
	j	StopChk
	
StopChk:	#check for user input errors
	la 	$t0, uGuess
	
	lb 	$t1, ($t0)			#load 0 bit to s1
	lb 	$t2, 1($t0)			#load 1st bit to s2
	lb 	$t3, 2($t0)			#load 2nd bit to s3
	lb 	$t4, 3($t0)			#load 3rd bit to s4
	
	#check if user typed STOP
	addi $t5, $zero, 83
	bne $t1, $t5, Errors
	addi $t5, $zero, 84
	bne $t2, $t5, Errors
	addi $t5, $zero, 79
	bne $t3, $t5, Errors
	addi $t5, $zero, 80
	beq $t4, $t5, DisplayAnswer
		
Errors:	#check for user input errors
	la 	$t0, uGuess
	
	lb 	$t1, ($t0)			#load 0 bit to t1
	lb 	$t2, 1($t0)			#load 1st bit to t2
	lb 	$t3, 2($t0)			#load 2nd bit to t3
	lb 	$t4, 3($t0)			#load 3rd bit to t4
	
	
# A = 65 , Z = 90 , a = 97 , and z = 122 for the ascii table. Use for err checking. 
# big ol mips 'if statements' check that input matches a-z ascii values
	addi 	$t5, $zero, 65
	blt 	$t1, $t5, InputErr
	addi 	$t5, $zero, 90
	ble 	$t1, $t5, Check02
	addi 	$t5, $zero, 97
	blt 	$t1, $t5, InputErr
	addi 	$t5, $zero, 122
	ble 	$t1, $t5, Check02
	j	InputErr
	#correct input	
	
Check02:				#check for invalid characters in the second bit
	addi 	$t5, $zero, 65
	blt 	$t2, $t5, InputErr
	addi 	$t5, $zero, 90
	ble 	$t2, $t5, Check03
	addi 	$t5, $zero, 97
	blt 	$t2, $t5, InputErr
	addi 	$t5, $zero, 122
	ble 	$t2, $t5, Check03
	j	InputErr
	
Check03:				#check for invalid characters in the third bit
	addi 	$t5, $zero, 65
	blt 	$t2, $t5, InputErr
	addi 	$t5, $zero, 90
	ble 	$t1, $t5, Check04
	addi 	$t5, $zero, 97
	blt 	$t1, $t5, InputErr
	addi 	$t5, $zero, 122
	ble 	$t1, $t5, Check04
	j	InputErr
	
Check04:				#check for invalid characters in the fourth bit
	addi 	$t5, $zero, 65
	blt 	$t1, $t5, InputErr
	addi 	$t5, $zero, 90
	ble 	$t1, $t5, Duplicate
	addi 	$t5, $zero, 97
	blt 	$t1, $t5, InputErr
	addi 	$t5, $zero, 122
	ble 	$t1, $t5, Duplicate
	j	InputErr
Duplicate:	
#check for duplicate letters in the users input
	beq $t1, $t2, DuplicateErr
	beq $t1, $t3, DuplicateErr
	beq $t1, $t4, DuplicateErr
	beq $t2, $t3, DuplicateErr
	beq $t2, $t4, DuplicateErr
	beq $t3, $t4, DuplicateErr
	
Continue:
	#clear all the t registers..
	li $t0, 0
	li $t1, 0
	li $t2, 0
	li $t3, 0
	li $t4, 0
	li $t5, 0

	
	jal BullCowCheck          		# will go to that spot, keep reference to that location
	move $a0, $v0 				# moving bull counter to the integer printing parameter
	move $a1, $v1				# moving cow counter to different register
	li $v0, 1				# preparing integer syscall
	syscall
	move $a0, $a1				# moving cow counter to integer printing parameter
	syscall
	
	li $v0,31 				#michaels beep
	la $t0,beep 
	lw $a0,0($t0) 
	addi $t2,$a0,12 
	la $t1,duration 
	lbu $a1,0($t1) 
	la $t3,volume 
	lbu $a3,0($t3) 
	move $t4,$a0 
	move $t5,$a1 
	move $t6,$a3 
	syscall 

	#check if there are four bulls. If there are, jump to winner
	add $s1, $zero, 4
	beq $t3, $s1, Winner
	
	j	gameLoop
	
	
BullCowCheck: 
	
	la	 $t1, Answer	      # load address for Answer b/c need to start from beginning again
	li 	 $t2, 0             # loads position counter; initalized to 0
	li 	 $t3, 0             # loads bulls counter; initialized to 0
	li	 $t4, 0             # loads cow counter; initialized to 0
	lb	 $t5, ($t1)	      # loading first character from Answer

	
LoopOne: 
	beq	 $t2, 4, LoopExit     # b/c at end of word
	li	 $t6, 0                # counter for loopTwo
	la	 $t0, uGuess        	# load address for UserGuess
	lb	 $t7, ($t0)            # load first character from uGuess
		
	j LoopTwo 
		
LoopOneEnd: 
	addi	 $t1, $t1, 1 # going to next character in Answer
	lb	 $t5, ($t1)	# loading next character in Answer
	addi	 $t2, $t2, 1 # incrementing position counter
	j LoopOne
		
LoopTwo: 
	beq	 $t6, 4, LoopOneEnd   # go back to outer loop
	beq	 $t2, $t6, BullCheck  # if position counter and letter are same
	bne 	 $t5, $t7, LoopTwoEnd    # first was incorrect, need to increment LoopTwo counter
	addi	 $t4, $t4, 1         # if not branching, then increment cow counter bc WE HAVE A COW
		
	j LoopOneEnd
		
LoopTwoEnd:
	addi	 $t6, $t6, 1         # incrementing LoopTwo counter
	addi	 $t0, $t0, 1         # going to next character in uGuess
	lb	 $t7, ($t0)            # load first character from uGuess
	j LoopTwo		# jump back to LoopOne because if match already found, remaining letters won't be a match no matter what
		
BullCheck:
	bne	 $t5, $t7, LoopTwoEnd    # first was incorrect, need to increment LoopTwo counter
	addi	 $t3, $t3, 1         # if not branching, then increment bull counter bc WE HAVE A BULL 
		
	j LoopTwoEnd		# jump back to LoopOne because if match already found, remaining letters won't be a match no matter what
		
LoopExit:
	move	 $v0, $t3		# moving bull counter
	move	 $v1, $t4		# moving cow counter
	jr	 $ra	

Winner:
	li	$v0, 30			# Get system time
	syscall				# Start timer
	lw	$t0, time03		# Number to divide time by
	div	$a0, $t0
	mfhi	$t1			# $t1 now contains ms in day
	li	$t0, 1000
	div	$t1, $t0
	mflo	$t1			# $t1 now contains seconds in day
	sub	$s2, $t1, $s2		# Get difference between start and end time, and store into $s2
	li	$v0, 4
	la	$a0, win
	syscall	
	li	$v0, 4
	la	$a0, time01
	syscall
	li	$v0, 1
	move	$a0, $s2
	syscall				# Print time spent in seconds
	li	$v0, 4
	la	$a0, time02
	syscall				# Print "seconds."
	
	j ShowMenu
	
DuplicateErr:
	li	 $v0, 4
	la	 $t0, GuessE02
	add	 $a0, $t0, $zero
	syscall
	
	j	gameLoop	
mError:					#for invalid menu entry
	li	 $v0, 4
	la	 $t0, MenuErr
	add	 $a0, $t0, $zero
	syscall
	
	j ShowMenu
	
Instructions:
	li	 $v0, 4
	la	 $t0, Inst
	add	 $a0, $t0, $zero
	syscall
	
	j ShowMenu

Exit:
	li	$v0, 4
	la	$t0, exit
	add	$a0, $t0, $zero
	syscall
	
	li	$v0, 10
	syscall	
	
InputErr:				#for invalid character entry
	li 	$v0, 4
	la 	$t0, GuessE04
	add	$a0, $t0, $zero
	syscall
	j	gameLoop
	
DisplayAnswer:
	li $v0, 4
	la $t0, quit
	add $a0, $t0, $zero
	syscall
	
	li $v0, 4
	la $t0, Answer
	add $a0, $t0, $zero
	syscall
	
	j ShowMenu			

	
ShowMenu:
	#begin time counter
	li 	$v0, 30				#get system time
	syscall
	lw 	$t0, time03			#calculate with miliseconds/day
	div 	$a0, $t0
	mfhi 	$s7				#$s2 converted to hold ms per day
	li 	$t0, 1000
	div 	$s7, $t0
	mflo 	$s7				#$s2 converted to hold s per day
	
	li 	$v0, 4
	la 	$t0, Menu00
	add	$a0, $t0, $zero
	syscall
	la 	$t0, Menu01
	add	$a0, $t0, $zero
	syscall
	la 	$t0, Menu02
	add	$a0, $t0, $zero
	syscall
	la 	$t0, Menu03
	add 	$a0, $t0, $zero
	syscall
	la 	$t0, mPrompt
	add 	$a0, $t0, $zero
	syscall
	li 	$v0, 8			#Loading the input as a string to avoid problems
	la 	$a0, uGuess
	li 	$a1, 64
	syscall				#storing string in UserGuess
	lb 	$t1, ($a0)		#just pulling the first byte
	
	addi 	$t0, $zero, 49
	beq 	$t1, $t0, gameLoop  	#starting the game
	addi 	$t0, $zero, 50	
	beq 	$t1, $t0, Instructions	#Showing instructions
	addi 	$t0, $zero, 51	
	beq	$t1, $t0, Exit		#call the exit function and exit the program
	addi	$t0, $zero, 52
	bne 	$t1, $t0, mError	#prints error for wrong input
				
	#li 	$v0, 10			#exits the program
	#syscall
	jal main
	
